document.addEventListener('DOMContentLoaded', function() {
  // Hiệu ứng nút RSVP
  const rsvpButton = document.getElementById('rsvp-btn');
  
  rsvpButton.addEventListener('click', function() {
    alert('Cảm ơn bạn đã xác nhận tham dự!');
    // Đây là nơi bạn có thể thêm mã để gửi form xác nhận tham dự
  });
  
  // Hiệu ứng khi cuộn trang
  function fadeInElements() {
    const elements = document.querySelectorAll('.wedding-invitation > *, .names-container > *');
    elements.forEach((element, index) => {
      // Thêm class ban đầu cho animation
      element.style.opacity = '0';
      element.style.transform = 'translateY(20px)';
      
      // Thêm transition
      element.style.transition = 'opacity 0.5s ease, transform 0.5s ease';
      
      // Thực hiện animation
      setTimeout(() => {
        element.style.opacity = '1';
        element.style.transform = 'translateY(0)';
      }, 100 * index);
    });
  }
  
  // Thêm hiệu ứng fade-in khi trang tải xong
  window.addEventListener('load', function() {
    fadeInElements();
  });
  
  // Thêm hiệu ứng hover cho các phần tử
  const detailItems = document.querySelectorAll('.detail-item');
  detailItems.forEach(item => {
    item.addEventListener('mouseenter', function() {
      this.style.transform = 'translateY(-5px)';
    });
    
    item.addEventListener('mouseleave', function() {
      this.style.transform = 'translateY(0)';
    });
  });
  
  // Thêm hiệu ứng lấp lánh cho các yếu tố trang trí
  function addSparkles() {
    const invitation = document.querySelector('.wedding-invitation');
    
    for (let i = 0; i < 20; i++) {
      const sparkle = document.createElement('div');
      sparkle.className = 'sparkle';
      
      // Vị trí ngẫu nhiên
      const left = Math.random() * 100;
      const top = Math.random() * 100;
      
      // Kích thước ngẫu nhiên
      const size = Math.random() * 5 + 2;
      
      // Thời gian animation ngẫu nhiên
      const duration = Math.random() * 3 + 2;
      
      sparkle.style.cssText = `
        position: absolute;
        left: ${left}%;
        top: ${top}%;
        width: ${size}px;
        height: ${size}px;
        background-color: rgba(212, 175, 55, 0.7);
        border-radius: 50%;
        opacity: 0;
        animation: sparkle ${duration}s infinite;
      `;
      
      invitation.appendChild(sparkle);
    }
  }
  
  // Thêm keyframes cho animation lấp lánh
  const style = document.createElement('style');
  style.textContent = `
    @keyframes sparkle {
      0% { transform: scale(0); opacity: 0; }
      50% { transform: scale(1); opacity: 0.8; }
      100% { transform: scale(0); opacity: 0; }
    }
  `;
  document.head.appendChild(style);
  
  // Thêm hiệu ứng lấp lánh
  addSparkles();
});

