document.addEventListener('DOMContentLoaded', function() {
  // Thay thế màu chủ đạo trong các ornament SVG
  const mainColor = getComputedStyle(document.documentElement).getPropertyValue('--main-color') || '{{màu_chủ_đạo}}';
  const ornaments = document.querySelectorAll('.ornament');
  
  ornaments.forEach(ornament => {
    const style = getComputedStyle(ornament);
    const bgImage = style.backgroundImage;
    
    if (bgImage.includes('svg')) {
      // Thay thế màu trong SVG
      const newBgImage = bgImage.replace(/%23d4af37/g, encodeURIComponent(mainColor.trim()));
      ornament.style.backgroundImage = newBgImage;
    }
  });
  
  // Hiệu ứng hiển thị từng phần của thiệp
  const sections = [
    document.querySelector('.header'),
    document.querySelector('.couple-section'),
    document.querySelector('.date-section'),
    document.querySelector('.venue-section'),
    document.querySelector('.footer')
  ];
  
  sections.forEach((section, index) => {
    if (section) {
      setTimeout(() => {
        section.style.opacity = '1';
      }, 300 * index);
    }
  });
});

