@import url('https://fonts.googleapis.com/css2?family=Carattere&family=Corinthia:wght@400;700&display=swap');

:root {
  --primary-color: {{màu_chủ_đạo}};
  --secondary-color: {{màu_phụ}};
  --accent-color: {{màu_nhấn}};
  --background-color: {{màu_nền}};
  --text-color: {{màu_chữ}};
  --light-color: #ffffff;
  --dark-color: #1a1a1a;
  --border-color: rgba(191, 149, 63, 0.3);
  --shadow-color: rgba(0, 0, 0, 0.1);
  
  --title-font: 'Carattere', cursive;
  --heading-font: 'Carattere', cursive;
  --handwriting-font: 'Corinthia', cursive;
  --body-font: 'Corinthia', cursive;
  --sans-font: 'Corinthia', cursive;
  
  --section-spacing: 80px;
  --element-spacing: 30px;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: var(--body-font);
  color: var(--text-color);
  line-height: 1.6;
  background-color: var(--background-color);
}

/* Cấu trúc chung cho các phần */
.section {
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 40px 20px;
  position: relative;
  overflow: hidden;
}

.section-inner {
  max-width: 1000px;
  width: 100%;
  margin: 0 auto;
  position: relative;
}

.section-title {
  font-family: var(--heading-font);
  font-size: 36px;
  font-weight: 600;
  text-align: center;
  margin-bottom: var(--element-spacing);
  color: var(--primary-color);
  position: relative;
  padding-bottom: 15px;
}

.section-title:after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 100px;
  height: 2px;
  background: var(--primary-color);
}

.page-indicator {
  display: flex;
  justify-content: center;
  margin-top: var(--element-spacing);
}

.indicator {
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background-color: var(--border-color);
  margin: 0 5px;
  transition: all 0.3s ease;
}

.indicator.active {
  background-color: var(--primary-color);
  transform: scale(1.2);
}

/* Phần 1: Trang bìa */
.cover-background {
  background-color: var(--light-color);
  border: 1px solid var(--border-color);
  padding: 60px;
  box-shadow: 0 20px 50px var(--shadow-color);
  position: relative;
  overflow: hidden;
}

.cover-background:before {
  content: '';
  position: absolute;
  top: 10px;
  left: 10px;
  right: 10px;
  bottom: 10px;
  border: 1px solid var(--border-color);
  pointer-events: none;
}

.cover-content {
  text-align: center;
  position: relative;
  z-index: 2;
}

.main-title {
  font-family: var(--title-font);
  font-size: 60px;
  color: var(--primary-color);
  margin-bottom: var(--element-spacing);
  line-height: 1.2;
  text-shadow: 1px 1px 2px rgba(0,0,0,0.1);
}

.couple-names {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
  margin-bottom: var(--element-spacing);
}

.bride-name, .groom-name {
  font-family: var(--handwriting-font);
  font-size: 70px;
  font-weight: 700;
  color: var(--primary-color);
  margin: 0 10px;
  line-height: 1;
}

.ampersand {
  font-family: var(--handwriting-font);
  font-size: 80px;
  color: var(--accent-color);
  margin: 0 20px;
  line-height: 1;
  display: block;
  width: 100%;
}

.couple-photo {
  margin: var(--element-spacing) auto;
  max-width: 400px;
  position: relative;
}

.cover-photo {
  width: 100%;
  height: auto;
  border-radius: 10px;
  box-shadow: 0 10px 30px var(--shadow-color);
  border: 5px solid var(--light-color);
}

.wedding-date, .wedding-time {
  margin: var(--element-spacing) auto;
  max-width: 400px;
  background-color: rgba(255, 255, 255, 0.9);
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 5px 15px var(--shadow-color);
  border: 1px solid var(--border-color);
}

.date-line, .time-line {
