}

.date-label, .time-label {
  font-family: var(--sans-font);
  font-size: 16px;
  font-weight: 500;
  color: var(--secondary-color);
}

.date-value, .time-value {
  font-family: var(--heading-font);
  font-size: 18px;
  font-weight: 600;
  color: var(--primary-color);
}

/* Phần 2: Giới thiệu gia đình */
.family-background {
