    height: 1px;
  }
  
  .love-quote {
    font-size: 30px;
  }
  
  .venue-details {
    flex-direction: column;
  }
}

/* Animation Classes */
.fade-in {
  opacity: 0;
  animation: fadeIn 1.5s forwards;
}

.slide-up {
  opacity: 0;
  transform: translateY(30px);
  animation: slideUp 1.5s forwards;
}

.scale-in {
  opacity: 0;
  transform: scale(0.9);
  animation: scaleIn 1.5s forwards;
}

@keyframes fadeIn {
  to {
    opacity: 1;
  }
}

@keyframes slideUp {
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scaleIn {
  to {
    opacity: 1;
    transform: scale(1);
  }
}

