document.addEventListener('DOMContentLoaded', function() {
  // Thêm các class animation cho các phần tử
  const animatedElements = [
    { selector: '.main-title', className: 'fade-in', delay: 0 },
    { selector: '.couple-names', className: 'scale-in', delay: 300 },
    { selector: '.cover-photo', className: 'fade-in', delay: 600 },
    { selector: '.wedding-date', className: 'slide-up', delay: 900 },
    { selector: '.wedding-time', className: 'slide-up', delay: 1200 },
    { selector: '.section-title', className: 'fade-in', delay: 0 },
    { selector: '.family-side', className: 'slide-up', delay: 300 },
    { selector: '.love-quote', className: 'fade-in', delay: 600 },
    { selector: '.family-photo', className: 'scale-in', delay: 900 },
    { selector: '.venue-time', className: 'slide-up', delay: 300 },
    { selector: '.venue-location', className: 'slide-up', delay: 600 },
    { selector: '.map-container', className: 'fade-in', delay: 900 },
    { selector: '.venue-photo', className: 'scale-in', delay: 1200 },
    { selector: '.thanks-message', className: 'fade-in', delay: 300 },
    { selector: '.thanks-photo', className: 'scale-in', delay: 600 },
    { selector: '.rsvp-section', className: 'slide-up', delay: 900 }
  ];
  
  // Thêm các class animation khi phần tử xuất hiện trong viewport
  const observer = new IntersectionObserver((entries) => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        const section = entry.target;
        const sectionId = section.id;
        
        animatedElements.forEach(item => {
          const elements = section.querySelectorAll(item.selector);
          elements.forEach(element => {
            setTimeout(() => {
              element.classList.add(item.className);
            }, item.delay);
          });
        });
        
        // Cập nhật indicator
        document.querySelectorAll('.indicator').forEach(indicator => {
          indicator.classList.remove('active');
        });
        
        const activeIndicators = document.querySelectorAll(`.${sectionId} .indicator`);
        if (activeIndicators.length > 0) {
          const index = Array.from(document.querySelectorAll('.section')).findIndex(s => s.id === sectionId);
          if (index >= 0) {
            const indicators = document.querySelectorAll('.page-indicator');
            indicators.forEach(indicatorSet => {
              const dots = indicatorSet.querySelectorAll('.indicator');
              if (dots[index]) {
                dots[index].classList.add('active');
              }
            });
          }
        }
        
        observer.unobserve(section);
      }
    });
  }, { threshold: 0.3 });
});

